        box-shadow: 0 6px 18px rgba(211, 137, 156, 0.25),
          inset 0 2px 4px rgba(255, 255, 255, 0.7);
        backdrop-filter: blur(12px);
        -webkit-backdrop-filter: blur(12px);
        transition: all 0.3s ease-in-out;
        position: relative;
