      .cat-collapsible:hover {
        box-shadow: 0 8px 20px rgba(211, 137, 156, 0.3),
          inset 0 2px 4px rgba(255, 255, 255, 0.7);
      }

      .cat-header {
        position: relative; padding: 12px 20px; cursor: pointer;
        display: flex; align-items: center; justify-content: space-between;
        border-bottom: 1px dashed rgba(255, 192, 203, 0.5);
        transition: all 0.3s ease;
      }

      .cat-header:hover {
        background: rgba(255, 255, 255, 0.2);
      }

      .cat-title {
        display: flex; align-items: center; gap: 10px;
        font-size: 18px; font-weight: bold; color: #c58798;
        text-shadow: 0 1px 1px rgba(255, 255, 255, 0.9);
      }

      .cat-title-icon {
        height: 28px; 
        width: auto;  
        max-width: 40px; 
        vertical-align: middle; 
        filter: drop-shadow(0 1px 1px rgba(0, 0, 0, 0.1));
        transition: opacity 0.3s ease; 
      }

      .toggle-icon {
        width: 28px; height: 28px; border-radius: 50%;
        background: linear-gradient(145deg, #ffccd5, #ffb3c1);
        display: flex; align-items: center; justify-content: center;
        transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
        box-shadow: 0 3px 6px rgba(255, 179, 193, 0.5);
        position: relative;
      }
      .toggle-icon::before {
        content: '+'; color: white; font-size: 20px; font-weight: bold;
        line-height: 1; text-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
        transition: transform 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
      }
      .cat-collapsible.expanded .toggle-icon {
        background: linear-gradient(145deg, #ffb3c1, #ff9aa2);
        transform: scale(1.1) rotate(360deg);
      }
      .cat-collapsible.expanded .toggle-icon::before {
        transform: rotate(135deg);
      }
      .cat-content {
        max-height: 0; overflow: hidden;
        transition: max-height 0.5s cubic-bezier(0.4, 0, 0.2, 1);
        background-image:
          linear-gradient(to right, rgba(255, 192, 203, 0.3) 1px, transparent 1px),
          linear-gradient(to bottom, rgba(255, 192, 203, 0.3) 1px, transparent 1px),
          linear-gradient(to bottom, rgba(255, 248, 250, 0.9) 0%, rgba(255, 235, 240, 1) 100%),
          radial-gradient(rgba(255, 192, 203, 0.4) 1px, transparent 1px);
        background-size: 20px 20px, 20px 20px, auto, 10px 10px;
      }
      .cat-content-inner {
        padding: 20px 25px 0; color: #a07383;
        font-size: 17px; position: relative;
        white-space: normal; word-wrap: break-word;
        word-break: break-word; overflow-wrap: break-word;
      }
      .cat-content-inner p {
        margin: 0 0 0.75em 0; padding: 0;
        text-indent: 2em; line-height: 1.7;
      }
      .cat-content-inner p:last-child { margin-bottom: 0; }
      .cat-content-inner::before {
        content: '🐾'; position: absolute; top: 10px;
        right: 15px; font-size: 12px; opacity: 0.6;
      }
      .cat-footer-toggle {
        display: block; text-align: center; padding: 15px 0;
        color: #c58798; opacity: 0.8; font-size: 16px;
        cursor: pointer; transition: all 0.2s ease;
        user-select: none;
      }
      .cat-footer-toggle:hover {
        opacity: 1; transform: scale(1.05);
      }
      .cat-footer-toggle:active {
        transform: scale(0.98);
      }
    </style>
