</head>
<body>
<!-- disable-default-loading -->
<div class="music-player-container">
    <img id="playerBackgroundImage" class="player-background-image" src="https://sharkpan.xyz/f/A1MYsZ/IMG_20250615_021028.png" alt="Player Background">
    <div class="player-content">
        <div class="main-controls-wrapper">
            <div class="song-info">
                <p class="current-song" id="currentSongDisplay">选择歌曲</p>
                <div class="time-display">
                    <span id="currentTime">0:00</span> / <span id="duration">0:00</span>
                </div>
            </div>
            <div class="player-controls">
                <button class="control-button" id="prevButton" title="上一首">⏮</button>
                <button class="control-button" id="playPauseButton" title="播放/暂停">▶</button>
                <button class="control-button" id="nextButton" title="下一首">⏭</button>
            </div>
        </div>

        <div class="progress-container" id="progressContainer">
            <div class="progress-bar" id="progressBar"></div>
        </div>

        <div class="utility-buttons">
            <button class="utility-button" id="addSongButton">添加</button>
            <button class="utility-button" id="togglePlaylistButton">列表</button>
            <button class="utility-button" id="playModeButton" title="播放模式">循环</button>
            <button class="utility-button" id="autoplayButton" title="切换自动播放">自动</button>
            <button class="utility-button" id="background-change-button">背景</button>
        </div>

        <div id="playlistDisplay"></div>
        <audio id="myAudio"></audio>
    </div>
</div>

<script>
    // --- DOM Elements ---
    const audio = document.getElementById('myAudio');
    const playerBackgroundImage = document.getElementById('playerBackgroundImage');
    const playPauseButton = document.getElementById('playPauseButton');
    const prevButton = document.getElementById('prevButton');
    const nextButton = document.getElementById('nextButton');
    const addSongButton = document.getElementById('addSongButton');
    const bgChangeButton = document.getElementById('background-change-button');
    const togglePlaylistButton = document.getElementById('togglePlaylistButton');
    const playModeButton = document.getElementById('playModeButton');
    const autoplayButton = document.getElementById('autoplayButton');
    const progressContainer = document.getElementById('progressContainer');
    const progressBar = document.getElementById('progressBar');
    const currentTimeSpan = document.getElementById('currentTime');
    const durationSpan = document.getElementById('duration');
    const currentSongDisplay = document.getElementById('currentSongDisplay');
    const playlistDisplay = document.getElementById('playlistDisplay');

    // --- Default Playlist & State ---
    let defaultPlaylistTracks = [
        { url: 'https://files.catbox.moe/hx9mm1.flac' }, { url: 'https://files.catbox.moe/yibpr6.mp3' },
        { url: 'https://files.catbox.moe/773x8i.mp3' }, { url: 'https://files.catbox.moe/i3uh1l.mp3' },
        { url: 'https://files.catbox.moe/uxx41m.mp3' }, { url: 'https://files.catbox.moe/umgilk.mp3' },
        { url: 'https://files.catbox.moe/7tqpv3.mp3' }, { url: 'https://files.catbox.moe/c9cw3d.mp3' },
        { url: 'https://files.catbox.moe/ifqvr3.mp3' }
    ];
    defaultPlaylistTracks.forEach((track, index) => { track.name = `🎶${index + 1}`; });

    let playlist = [];
    let currentTrackIndex = -1;
    let playMode = 'repeat';
    let autoplayEnabled = true;

    // --- Storage Keys (Using V7 to ensure fresh state) ---
    const playlistVarKey = 'musicPlayerPlaylist_v7';
    const backgroundVarKey = 'musicPlayerBackground_v7';
    const playModeVarKey = 'musicPlayerMode_v7';
    const currentTrackVarKey = 'musicPlayerCurrentTrack_v7';
    const playbackPositionVarKey = 'musicPlayerPosition_v7';
    const autoplayVarKey = 'musicPlayerAutoplay_v7';

    // --- Helper Functions ---
    function formatTime(seconds) {
        if (isNaN(seconds) || seconds < 0) return "0:00";
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = Math.floor(seconds % 60);
        return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
    }

    async function saveToGlobalVar(key, value) {
        try { await triggerSlash(`/setglobalvar key=${key} ${typeof value === 'object' ? JSON.stringify(value) : String(value)}`); }
        catch (e) { console.error(`Error saving ${key}:`, e); }
    }

    async function getFromGlobalVar(key) {
        try {
            const result = await triggerSlashWithResult(`/getglobalvar ${key}`);
            if (!result || result === 'undefined' || result.trim() === '') return null;
            try { return JSON.parse(result); } catch { return result; }
        } catch (e) { console.error(`Error loading ${key}:`, e); return null; }
    }

    // --- Player Logic ---
    async function setPlayMode(mode) {
        playMode = mode;
        updatePlayModeButton();
        await saveToGlobalVar(playModeVarKey, mode);
    }

    function updatePlayModeButton() {
        const modeMap = {'repeat': ['循环', '列表循环'], 'random': ['随机', '随机播放'], 'single': ['单曲', '单曲循环'], 'stop': ['停止', '播完停止']};
        playModeButton.textContent = modeMap[playMode][0];
        playModeButton.title = modeMap[playMode][1];
    }

    async function setAutoplay(enabled) {
        autoplayEnabled = enabled;
        updateAutoplayButton();
        await saveToGlobalVar(autoplayVarKey, enabled);
    }

    function updateAutoplayButton() {
        autoplayButton.classList.toggle('active', autoplayEnabled);
        autoplayButton.textContent = autoplayEnabled ? '自动' : '手动';
        autoplayButton.title = `自动播放已${autoplayEnabled ? '开启' : '关闭'}`;
    }

    async function saveBackground(imageUrl) {
        if (imageUrl && imageUrl.trim()) {
            playerBackgroundImage.src = imageUrl.trim();
            await saveToGlobalVar(backgroundVarKey, imageUrl.trim());
        }
    }

    async function loadBackground() {
        const savedBackground = await getFromGlobalVar(backgroundVarKey);
        if (savedBackground) await saveBackground(savedBackground);
    }

    async function savePlaybackState() {
        if (currentTrackIndex >= 0) {
            await saveToGlobalVar(currentTrackVarKey, currentTrackIndex);
            await saveToGlobalVar(playbackPositionVarKey, audio.currentTime);
        }
    }

    async function loadPlaylist() {
        try {
            const globalPlaylist = await getFromGlobalVar(playlistVarKey);
            playlist = (globalPlaylist && Array.isArray(globalPlaylist) && globalPlaylist.length > 0)
                ? globalPlaylist
                : JSON.parse(JSON.stringify(defaultPlaylistTracks));
        } catch (e) {
            console.error('Error loading playlist:', e);
            playlist = JSON.parse(JSON.stringify(defaultPlaylistTracks));
        }
        updateUI();

        const savedMode = await getFromGlobalVar(playModeVarKey);
        if (savedMode) playMode = savedMode;
        updatePlayModeButton();

        const savedAutoplay = await getFromGlobalVar(autoplayVarKey);
        if (savedAutoplay !== null) autoplayEnabled = !!savedAutoplay;
        updateAutoplayButton();

        await loadBackground();
        await loadPlaybackState();
    }

    async function loadPlaybackState() {
        const savedIndex = await getFromGlobalVar(currentTrackVarKey);
        const savedPosition = await getFromGlobalVar(playbackPositionVarKey);
        if (savedIndex !== null && savedIndex >= 0 && savedIndex < playlist.length) {
            loadTrack(savedIndex, false); // Load without autoplaying
            if (savedPosition !== null && !isNaN(savedPosition)) {
                audio.addEventListener('loadedmetadata', () => {
                   if (audio.duration && savedPosition < audio.duration) audio.currentTime = savedPosition;
                }, { once: true });
            }
        } else if (playlist.length > 0 && autoplayEnabled) { playTrack(0); }
    }

    function loadTrack(index, shouldAutoplay = autoplayEnabled) {
        if (!playlist[index] || !playlist[index].url) { currentTrackIndex = -1; updateUI(); return; }
        currentTrackIndex = index;
        audio.src = playlist[currentTrackIndex].url;
        audio.load();
        updateUI();
        savePlaybackState();
        if (shouldAutoplay) { audio.play().catch(e => { triggerSlash(`/echo 自动播放失败: ${e.message}`); }); }
    }

    function playTrack(index) {
        if (playlist.length === 0) { triggerSlash(`/echo 列表为空`); return; }
        if (index < 0 || index >= playlist.length) return;
        if (index !== currentTrackIndex) { loadTrack(index, true); }
        else if (audio.paused) { audio.play().catch(e => { triggerSlash(`/echo 播放失败: ${e.message}`); }); }
    }

    function updateUI() {
        const track = playlist[currentTrackIndex];
        currentSongDisplay.textContent = track ? track.name : (playlist.length > 0 ? "选择歌曲" : "列表为空");
        currentSongDisplay.title = track ? (track.name || track.url || '') : "";
        updateTimeDisplay();
        updateProgressBar();
        updatePlaylistDisplay();
    }

    function updateTimeDisplay() {
        durationSpan.textContent = formatTime(audio.duration || 0);
        currentTimeSpan.textContent = formatTime(audio.currentTime || 0);
    }
    function updateProgressBar() { progressBar.style.width = (audio.duration > 0) ? (audio.currentTime / audio.duration) * 100 + '%' : '0%'; }
    function updatePlaylistDisplay() {
        playlistDisplay.innerHTML = "";
        playlist.forEach((track, index) => {
            const itemDiv = document.createElement('div');
            itemDiv.className = 'playlist-item' + (index === currentTrackIndex ? ' playing' : '');
            itemDiv.onclick = () => playTrack(index);
            const nameSpan = document.createElement('span');
            nameSpan.className = 'playlist-item-name';
            nameSpan.textContent = track.name || '未知歌曲';
            nameSpan.title = track.url || '';
            const deleteButton = document.createElement('button');
            deleteButton.className = 'delete-track-button';
            deleteButton.innerHTML = '×';
            deleteButton.title = '删除';
            deleteButton.onclick = e => { e.stopPropagation(); deleteTrack(index); };
            itemDiv.append(nameSpan, deleteButton);
            playlistDisplay.appendChild(itemDiv);
        });
    }

    async function deleteTrack(indexToDelete) {
        if (indexToDelete < 0 || indexToDelete >= playlist.length) return;
        if (await triggerSlashWithResult(`/buttons labels=["确认","取消"] 删除 "${playlist[indexToDelete].name || '该歌曲'}"?`) !== '确认') return;

        playlist.splice(indexToDelete, 1);
        if (indexToDelete === currentTrackIndex) {
            audio.pause(); audio.src = ''; currentTrackIndex = -1;
            if (playlist.length > 0) loadTrack(Math.min(indexToDelete, playlist.length - 1), false);
            else updateUI();
        } else if (indexToDelete < currentTrackIndex) { currentTrackIndex--; }
        await savePlaylistAndUI();
    }

    async function addSong() {
        const input = await triggerSlashWithResult(`/input large=on 请输入歌曲链接，每行一首:`);
        if (!input || !input.trim()) return;
        const urls = input.trim().split('\n').map(line => line.trim()).filter(Boolean);
        const newTracks = urls.filter(url => !playlist.some(t => t.url === url)).map(url => ({ url, name: '' }));

        if (newTracks.length === 0) { triggerSlash(`/echo 未添加歌曲(可能重复或输入为空)`); return; }
        playlist.push(...newTracks);
        await savePlaylistAndUI();
        if (currentTrackIndex === -1 && playlist.length > 0) loadTrack(playlist.length - newTracks.length, false);
        triggerSlash(`/echo 添加${newTracks.length}首`);
    }

    async function savePlaylistAndUI() {
        playlist.forEach((track, index) => { track.name = `🎶${index + 1}`; });
        updatePlaylistDisplay();
        await saveToGlobalVar(playlistVarKey, playlist);
        await savePlaybackState();
    }

    // --- Event Listeners ---
    addSongButton.addEventListener('click', addSong);
    prevButton.addEventListener('click', () => { if (playlist.length > 0) playTrack((currentTrackIndex - 1 + playlist.length) % playlist.length); });
    nextButton.addEventListener('click', () => { if (playlist.length > 0) playTrack((currentTrackIndex + 1) % playlist.length); });
    playPauseButton.addEventListener('click', () => { if (audio.paused) playTrack(currentTrackIndex === -1 ? 0 : currentTrackIndex); else audio.pause(); });
    audio.addEventListener('play', () => { playPauseButton.innerHTML = '❚❚'; });
    audio.addEventListener('pause', () => { playPauseButton.innerHTML = '▶'; });
    audio.addEventListener('ended', () => {
        let next = -1;
        switch(playMode) {
            case 'repeat': next = (currentTrackIndex + 1) % playlist.length; break;
            case 'random':
                if (playlist.length > 1) { do { next = Math.floor(Math.random() * playlist.length); } while(next === currentTrackIndex); }
                else if (playlist.length === 1) next = 0; break;
            case 'single': next = currentTrackIndex; break;
        }
        if (next !== -1) playTrack(next);
    });
    progressContainer.addEventListener('click', e => {
        if (audio.duration) {
            const rect = progressContainer.getBoundingClientRect(); // Get the bounding box of the progress container
            const offsetX = e.clientX - rect.left; // Calculate click position relative to the container
            audio.currentTime = (offsetX / progressContainer.clientWidth) * audio.duration;
        }
    });
    audio.addEventListener('timeupdate', () => { updateTimeDisplay(); updateProgressBar(); });
    audio.addEventListener('loadedmetadata', updateTimeDisplay);
    playModeButton.addEventListener('click', () => {
        const modes = ['repeat', 'random', 'single', 'stop'];
        setPlayMode(modes[(modes.indexOf(playMode) + 1) % modes.length]);
        triggerSlash(`/echo 播放模式: ${playModeButton.title}`);
    });
    autoplayButton.addEventListener('click', () => setAutoplay(!autoplayEnabled));
    togglePlaylistButton.addEventListener('click', () => {
        const isHidden = playlistDisplay.style.display === 'none';
        playlistDisplay.style.display = isHidden ? 'block' : 'none';
        togglePlaylistButton.textContent = isHidden ? '收起' : '列表';
        if (isHidden && playlist.length === 0) playlistDisplay.innerHTML = '<div style="text-align:center; color:#888; padding: 5px 0;">列表为空</div>';
    });
    bgChangeButton.addEventListener('click', async () => { const url = await triggerSlashWithResult('/input 新背景URL:'); if (url && url.trim()) await saveBackground(url); });
    setInterval(() => { if (!audio.paused) savePlaybackState(); }, 30000);
    window.addEventListener('beforeunload', () => { if (currentTrackIndex >= 0) savePlaybackState(); });

    // Multi-window sync
    const PLAYER_CHANNEL = "music_player_control_v7_sync"; // Updated channel name for uniqueness
    const playerId = Math.random().toString(36).substring(2, 9);
    eventOn(PLAYER_CHANNEL, (id, action) => { if (id !== playerId && action === 'play_event' && !audio.paused) audio.pause(); });
    const originalPlay = audio.play;
    audio.play = function(...args) { eventEmit(PLAYER_CHANNEL, playerId, 'play_event'); return originalPlay.apply(this, args); };

    // --- Initial Load ---
    loadPlaylist();
</script>
</body>
</html>
