    .falling-tag {
      position: absolute;
      color: #ff4141;
      animation: static-fall 6s linear infinite;
      font-size: 20px;
      font-weight: bold;
      pointer-events: none;
      user-select: none;
      text-shadow: 0 0 8px #ff4141;
      font-family: 'Orbitron', monospace;
