      <div class="details">
        <div class="artist-name">Don’t wake me up if this is a dream. Cause now I got everything


</div>
        <div class="lyrics">如果我在梦中就不要叫醒我，因为我得到了我想要的一切</div>

        <div class="controls">
          <button class="control-btn" id="playBtn">
            <i class="fas fa-play" id="playIcon"></i>
          </button>
        </div>

        <div class="volume-container">
          <i class="fas fa-volume-down"></i>
          <input type="range" id="volumeSlider" class="volume-slider" min="0" max="100" value="60" />
          <i class="fas fa-volume-up"></i>
        </div>
      </div>

      <audio id="audioPlayer" loop>
        <source src="https://files.catbox.moe/5ekhs2.mp3" />
      </audio>
    </div>
  </div>

  <script>
    const audio = document.getElementById('audioPlayer');
    const playBtn = document.getElementById('playBtn');
    const playIcon = document.getElementById('playIcon');
    const volumeSlider = document.getElementById('volumeSlider');
    const playerBox = document.getElementById('playerBox');

    audio.volume = volumeSlider.value / 100;

   // 自动播放尝试
    document.addEventListener('DOMContentLoaded', async () => {
      try {
        await audio.play();
        playIcon.className = 'fas fa-pause';
      } catch {
        console.log('自动播放失败，需要用户交互');
      }
    });

    playBtn.addEventListener('click', (e) => {
      e.stopPropagation(); // 防止点击事件冒泡到父元素，导致播放器折叠
      if (audio.paused) {
        audio.play().then(() => {
          playIcon.className = 'fas fa-pause';
        }).catch(error => console.log("播放失败: ", error));
      } else {
        audio.pause();
        playIcon.className = 'fas fa-play';
      }
    });

    volumeSlider.addEventListener('input', (e) => {
      e.stopPropagation();
      audio.volume = e.target.value / 100;
    });

    playerBox.addEventListener('click', (e) => {
      // 确保点击的不是按钮或滑块本身
      if (!e.target.closest('.control-btn') && !e.target.closest('.volume-slider')) {
        playerBox.classList.toggle('expanded');
      }
    });
  </script>
</body>
