    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.03);
}

.mes_text p strong::before,
.mes_text p strong::after {
    content: "â¦";
    color: rgba(167, 199, 231, 0.8);
    font-size: 0.8em;
    text-shadow: 0 0 6px rgba(167, 199, 231, 0.5);
}

.mes_text p strong::before { transform: scaleX(-1); }

.mes_text q {
    position: relative; 
    padding: 3px 9px;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.2));
    color: var(--text-primary);
