    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.05);
    display: inline-block;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.7);
}

.mes_text q::after {
    content: 'âœ¦';
    position: absolute;
    top: -1px;
    right: -8px;
    font-size: 1.5em;
    color: #a7c7e7;
    line-height: 0;
    text-shadow: 
        0 0 10px rgba(255, 255, 255, 1),
        0 0 20px rgba(167, 199, 231, 0.6),
        1px 1px 2px rgba(0, 0, 0, 0.1);
    transform: rotate(20deg);
    pointer-events: none;
}

/* ä»£ç å—æ ·å¼ - æ·¡è“ä¸»é¢˜ */
code {
