power_user.custom_css:
/* 感谢食用小狗爱的美化，接下来请阅读一下食用须知！！
    头图和id都可以自定义的，使用猫箱外链导入图片后找到有小狗爪🐾🐾的代码就可以自行更换啦！ 
    猫箱链接：https://catbox.moe/ 
*/

@import url("https://fontsapi.zeoseven.com/3/main/result.css");

body, 
#chat, 
.text_pole, 
textarea, 
select, 
.menu_button, 
h3, 
h4,
code,
.mes .ch_name .name_text,
.mes .mes_text {
    font-family: 'undefined', 'ChillRoundF 寒蝉全圆体', sans-serif !important; 
}



:root {
  --doc-height: 100%;
  --theme-bg: #f7faff;             /* 1. 核心背景色 (淡雅天蓝白) */
  --theme-shadow: #c4d3e0;         /* 2. 柔和阴影色 (软蓝灰) */
  --theme-highlight: #ffffff;      /* 3. 纯白高光色 */
  --chat-bubble-bg: #ffffff;      /* 4. 聊天气泡背景 (纯净白) */
  --ui-panel-bg: #f0f8ff;          /* 5. UI面板/卡片背景 (爱丽丝蓝) */
  --border-color: rgba(196, 211, 224, 0.5); /* 边框颜色 */
  --black70a: rgba(200, 238, 255, 0.7);
  --accent-blue: #a7c7e7;           /* 主强调色 (婴儿蓝) */
  --accent-red: #ff8a8a;            /* 警告/危险状态的主色 (柔和珊瑚红) */
  --accent-red-shadow: rgba(255, 138, 138, 0.4); /* 红色按钮的阴影/辉光 */
  --text-primary: #5b6b7f;       /* 主要文本 (深蓝灰) */
  --text-secondary: #9cb0c4;     /* 次要文本/时间戳 (中蓝灰) */
  --text-darkest: #3a506b;         /* 最深的文本/标题 (海军蓝灰) */
  --text-disabled: #d0d8e0;       /* 禁用状态 (浅蓝灰) */
  --white50a:  #d0d8e0;
}

#user-settings-block h4 {
  background-image: linear-gradient(348deg, 
    #A7D7F9, /* 像晴朗天空一样的、明亮的天蓝色 */
    #89CFF0  /* 经典的、柔和的Baby Blue，增加层次感 */
  );
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header {
  background-image: linear-gradient(348deg, 
    rgba(225, 245, 255, 0.4) 2%,  /* 1. 几乎透明的冰川水色 */
    rgba(200, 230, 255, 0.5) 10%, /* 2. 更轻柔的雾蓝色 */
    rgba(176, 224, 230, 0.6) 95%, /* 3. 保持通透感的粉末蓝 (Powder Blue) */
    rgba(135, 206, 235, 0.7) 100% /* 4. 收尾色，像天空的颜色 (Sky Blue) */
  );
}

#extensions_settings2 .inline-drawer-toggle.inline-drawer-header {
  background-image: linear-gradient(348deg, 
    rgba(225, 245, 255, 0.4) 2%,
    rgba(200, 230, 255, 0.5) 10%,
    rgba(176, 224, 230, 0.6) 95%,
    rgba(135, 206, 235, 0.7) 100%
  );
}


#curEditTextarea {
  background-color: #ffffff; /* 纯白背景保持不变 */
  color: #5F798D; /* 使用沉静的深蓝灰色文字，确保对比度 */
  border: 1px solid #D6EAF8; /* 使用非常浅的、柔和的蓝色边框 */
  padding: 12px;
  border-radius: 8px;
  box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.05); /* 内阴影保持不变 */
}


.margin0.title_restorable.standoutHeader {
  /* 关键：一个从浅到深的简单垂直渐变，营造柔和的晨光感 */
  background: linear-gradient(to bottom, #F0F8FF, #E1EFF9); /* 从爱丽丝蓝到淡雅的天蓝色 */
  color: #333; /* 深灰色文字保持不变 */
  border: none;
}

.standoutHeader {
  background: linear-gradient(to bottom, #F0F8FF, #E1EFF9);
  color: #5F798D; 
  border: none;
}


#chat,
#chat_container {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
  padding-top: 20px;
  padding-bottom: 20px;
}

.mes {
    position: relative;
    width: 94%; /* <--- 调整气泡宽度 */
    max-width: 700px;
    padding: 10px; /* <--- 内边距：文本和消息气泡的边距 */
    background: linear-gradient(180deg, #ffffff 0%, #fafdff 100%);
    border-radius: 16px; 
    border: 1px solid rgba(196, 211, 224, 0.5);
    border-top-color: rgba(255, 255, 255, 0.8);
    border-bottom-color: rgba(196, 211, 224, 0.5);
    box-shadow:
        inset 0 1px 1px rgba(255, 255, 255, 0.7),
        0 8px 25px rgba(196, 211, 224, 0.6),
        0 2px 5px rgba(196, 211, 224, 0.4);

    /* --- 以下为保持原有布局的Grid设置，无需改动 --- */
    display: grid;
    grid-template-columns: 45px 1fr;
    grid-template-rows: auto auto 1fr; 
    grid-template-areas:
        "avatar name-date"
        "avatar reasoning"
        "text text";
    align-items: start;
    gap: 0 12px;
}

.mes .mes_reasoning_details {
  grid-area: reasoning;
  padding-bottom: 10px; /* 给它和下面的正文之间增加一点间距 */
}

.mes .mesAvatarWrapper {
  grid-area: avatar;
}
/* 调整头像大小 */
.mes .mesAvatarWrapper .avatar,
.mes .mesAvatarWrapper .avatar img {
  width: 45px;
  height: 45px;
}

.mes .ch_name {
  grid-area: name-date;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
  padding: 0 20px 12px;
  border-bottom: 1px solid #eef2f7; /* 分割线颜色调整 */
}

.mes .mes_text {
  grid-area: text;
  padding: 12px 10px 10px;
  line-height: 1.6;
  box-sizing: border-box;
  color: var(--text-primary); /* 主要文本颜色 */
}
.mes .mes_block {
  display: contents;
}

.mes .ch_name {
  grid-area: name-date;
  position: relative; 
  display: flex;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
  min-height: 45px; 
}

/* 使用绝对定位将名字固定在左上角 */
.mes .ch_name .name_text {
  display: flex;
  flex-direction: column;
  line-height: 1.2;
  position: absolute; /* 关键：改为绝对定位 */
  top: 0px;             /* 关键：从容器顶部开始定位，位置固定 */
  left: 0;            /* 关键：从容器左侧开始定位 */
  font-weight: 600;
  font-size: 1.1em;
  color: var(--text-darkest); /* 名字颜色 */
}

/* 🐾🐾自定义文案在这里🐾🐾 */
.mes .ch_name .name_text::after {
  content: '逆风执炬 痛也如人意';
  font-size: 0.8em;
  font-weight: 400;
  color: var(--text-secondary); /* 次要文本颜色 */
}

.mes .ch_name .name_text {
  display: flex;
  flex-direction: column;
  line-height: 1.2;
  position: absolute;
  top: 0px;
  left: 0;
  font-weight: 600;
  font-size: 1.1em;
  color: var(--text-darkest); /* 名字颜色 */
}

.mes .ch_name .timestamp {
  position: absolute;
  top: 15px;      /* 与气泡顶部内边距对齐 */
  right: 15px;    /* 与气泡右侧内边距对齐 */
  font-size: 0.9em;
  color: var(--text-secondary); /* 次要文本颜色 */
}

.mes .mes_buttons {
    position: absolute;
    bottom: 8px;     /* 定位到气泡底部 */
    right: 15px;     /* 定位到气泡右侧 */
    z-index: 10;     /* 确保按钮在最上层 */
}

.mes .mes_buttons {
    bottom: 30px;
}

.mes .mes_text {
  padding-bottom: 30px;
}
.mes .mes_buttons {
    bottom: 30px;
}

.mes .mesIDDisplay,
.mes .mes_timer,
.mes .tokenCounterDisplay {
    position: absolute;
    bottom: 8px;
    font-size: 0.8em;
    color: var(--text-secondary); /* 次要文本颜色 */
    white-space: nowrap;
    padding-left: 16px; /* 为图标留出空间 */
}

.mes .mesIDDisplay::before,
.mes .mes_timer::before,
.mes .tokenCounterDisplay::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 12px;
    height: 12px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    filter: invert(41%) sepia(16%) saturate(695%) hue-rotate(176deg) brightness(96%) contrast(88%);
}

.mes .mesIDDisplay::before {
    background-image: url('https://files.catbox.moe/l70awr.png');
}
.mes .mes_timer::before {
    background-image: url('https://files.catbox.moe/dbhsd7.png');
}
.mes .tokenCounterDisplay::before {
    background-image: url('https://files.catbox.moe/xf0izy.png');
}

.mes[is_user="false"] .mesIDDisplay {
    left: 15px;
}
.mes[is_user="false"] .mes_timer {
    left: 75px;
}
.mes[is_user="false"] .tokenCounterDisplay {
    left: 145px;
}

.mes[is_user="true"] .mesIDDisplay {
    left: 15px;
}
.mes[is_user="true"] .tokenCounterDisplay {
    left: 75px; 
}

.mes[is_user="true"] .mes_timer::before {
    display: none;
}

.mes.user-message {
  background: linear-gradient(180deg, #fdfdff 0%, #f7faff 100%);
}

.mes[is_user="false"] .mes_text::before,
.mes[is_user="true"] .mes_text::before {
  content: '';
  display: block;

  /* 背景图通用样式 */
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;

  /* 尺寸与宽高比 */
  width: calc(100%-10px);
  max-width: 100％;
  height: 0;
  padding-top: 50%; /* 宽高比为 2:1，可根据需要调整 */
  margin-left: auto;
  margin-right: 0 auto 25px;
  border: 8px solid #e8f1f8;
  box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.1);
  border-radius: 8px;
}

/* 🐾🐾为char设置专属照片！🐾🐾 */
.mes[is_user="false"] .mes_text::before {
  background-image: url('https://files.catbox.moe/l9pph9.jpg'); 
}

/* 🐾🐾 为用户设置专属照片！🐾🐾 */
.mes[is_user="true"] .mes_text::before {
  background-image: url('https://files.catbox.moe/cw1ax7.jpg'); 
}



/* 斜体文本样式 - 淡蓝主题 */
.mes_text em {
    font-style: italic;
    color: var(--text-darkest);
    position: relative;
    padding: 0 3px 2px;
    background: linear-gradient(transparent 70%, rgba(167, 199, 231, 0.2) 70%); /* 淡蓝色背景高亮 */
}

.mes_text em::after {
    content: "";
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1.5px;
    background: repeating-linear-gradient(90deg,
        rgba(167, 199, 231, 0.7),
        rgba(167, 199, 231, 0.7) 4px,
        transparent 4px,
        transparent 8px );
    box-shadow: 0 0 3px rgba(167, 199, 231, 0.4);
}


/* 块级引用 (blockquote) - 淡蓝圆润设计 */
blockquote {
    position: relative;
    margin: 1.5em 0;
    padding: 20px 25px;
    background: linear-gradient(180deg, #ffffff 0%, #f7faff 100%);
    border-radius: 18px;
    box-shadow: 
        0 6px 12px rgba(196, 211, 224, 0.3),
        inset 0 1px 2px rgba(255, 255, 255, 0.9);
    border: 1px solid #eaf2fa;
    color: var(--text-primary);
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.9);
}

blockquote::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 8px;
    background: linear-gradient(90deg, 
        rgba(255, 255, 255, 0.6) 0%, 
        rgba(255, 255, 255, 0.9) 50%, 
        rgba(255, 255, 255, 0.6) 100%);
    border-radius: 18px 18px 0 0;
}

blockquote::after {
    content: "✿";
    position: absolute;
    bottom: 10px;
    right: 15px;
    color: rgba(167, 199, 231, 0.5);
    font-size: 1em;
    transform: rotate(15deg);
    text-shadow: 0 1px 2px rgba(255, 255, 255, 0.8);
}

blockquote p {
    margin: 0;
    line-height: 1.8;
    padding-left: 12px;
    border-left: 2px solid var(--accent-blue);
}


/* 协调其他样式 */
.mes_text p strong {
    font-weight: bold;
    color: var(--text-darkest);
    position: relative;
    padding: 2px 6px;
    margin: 0 3px;
    border-radius: 6px;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.4), transparent);
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.03);
}

.mes_text p strong::before,
.mes_text p strong::after {
    content: "❦";
    color: rgba(167, 199, 231, 0.8);
    font-size: 0.8em;
    text-shadow: 0 0 6px rgba(167, 199, 231, 0.5);
}

.mes_text p strong::before { transform: scaleX(-1); }

.mes_text q {
    position: relative; 
    padding: 3px 9px;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.2));
    color: var(--text-primary);
    border-radius: 8px;
    border: 1px solid rgba(255, 255, 255, 0.7);
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.05);
    display: inline-block;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.7);
}

.mes_text q::after {
    content: '✦';
    position: absolute;
    top: -1px;
    right: -8px;
    font-size: 1.5em;
    color: #a7c7e7;
    line-height: 0;
    text-shadow: 
        0 0 10px rgba(255, 255, 255, 1),
        0 0 20px rgba(167, 199, 231, 0.6),
        1px 1px 2px rgba(0, 0, 0, 0.1);
    transform: rotate(20deg);
    pointer-events: none;
}

/* 代码块样式 - 淡蓝主题 */
code {
    background: linear-gradient(180deg, #fafdff 0%, #f0f8ff 100%);
    color: #6a7b90;
    border-radius: 10px;
    padding: 5px 10px;
    position: relative;
    line-height: 1.5;
    box-shadow: 
        0 3px 6px rgba(196, 211, 224, 0.3),
        inset 0 1px 1px rgba(255, 255, 255, 0.9);
    border: 1px solid #eaf2fa;
    border-top: 1px solid rgba(255, 255, 255, 0.8);
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.9);
}



code::after {
    content: "";
    position: absolute;
    left: 0;
    bottom: 0;
    width: 100%;
    height: 40%;
    background: linear-gradient(transparent, rgba(255, 255, 255, 0.3));
    border-radius: 0 0 10px 10px;
    pointer-events: none;
}
code br { content: ""; margin: 0.5em 0; display: block; }

#top-bar {
    background: transparent !important;      
    border: none !important;              
    box-shadow: none !important;          
    -webkit-backdrop-filter: none !important;
}

.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }

.drawer-icon.fa-fw {
    width: 35px;
    height: 35px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: transform 0.3s ease;
    opacity: 0.7; /* 让图标颜色更柔和 */
}
.drawer-icon.fa-fw:hover { opacity: 1; transform: scale(1.1); }

#ai-config-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#sys-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
#advanced-formatting-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#WI-SP-button .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
#user-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#logo_block .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
#extensions-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#table_database_settings_drawer .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
#persona-management-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#rightNavHolder .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }

#options_button::before,
#extensionsMenuButton::before,
#send_but::before,
#quick-reply-rocket-button > i { display: none !important; }

#options_button,
#extensionsMenuButton,
#send_but,
#quick-reply-rocket-button {
    width: 60px;
    height: 60px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: transform 0.2s ease, opacity 0.2s;
    font-size: 0;
    border: none;
    background-color: transparent;
    opacity: 0.8;
}

#options_button:hover,
#extensionsMenuButton:hover,
#send_but:hover,
#quick-reply-rocket-button:hover {
    transform: scale(1.1);
    opacity: 1;
}

#options_button { background-image: url('https://files.catbox.moe/n6c3iw.png'); }
#extensionsMenuButton { background-image: url('https://files.catbox.moe/41vit4.png'); }
#send_but { background-image: url('https://files.catbox.moe/bdrnch.png'); }
#quick-reply-rocket-button { background-image: url('https://files.catbox.moe/ddtnsa.png'); }

/* --- 通用输入框提示词自定义 --- */
#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "˖˚. ❅ 𝘞𝘪𝘯𝘵𝘦𝘳 𝘈𝘯𝘯𝘪𝘷𝘦𝘳𝘴𝘢𝘳𝘺. ‧₊˚⊹";
    color: var(--text-disabled);
    left: 100px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-style: italic;
    font-size: 0.4em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
}



input[type="checkbox"] {
    appearance: none; -webkit-appearance: none; position: relative; cursor: pointer;
    vertical-align: middle; margin-right: 8px; width: 22px; height: 22px;
    border-radius: 8px;
    background-color: #f7faff;
    transition: all 0.2s ease-in-out;
    border: none;
    box-shadow: 3px 3px 6px #c4d3e0, -3px -3px 6px #ffffff;
}
input[type="checkbox"]:hover { box-shadow: 4px 4px 8px #c4d3e0, -4px -4px 8px #ffffff; }
input[type="checkbox"]:active { transform: scale(0.95); }
input[type="checkbox"]:checked { box-shadow: inset 3px 3px 6px #c4d3e0, inset -3px -3px 6px #ffffff; }
input[type="checkbox"]::after {
    content: ''; position: absolute; transform: translate(-50%, -50%) scale(0);
    transition: transform 0.1s ease-in;
}
input[type="checkbox"]:checked::after {
    top: 50%; left: 50%; width: 12px; height: 12px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%235b6b7f" stroke-width="3" stroke-linecap="round" stroke-linejoin="round"><polyline points="20 6 9 17 4 12"></polyline></svg>');
    background-size: contain; background-repeat: no-repeat;
    transform: translate(-50%, -50%) scale(1);
}

input[type="radio"] {
    appearance: none; -webkit-appearance: none; position: relative; cursor: pointer;
    vertical-align: middle; margin-right: 8px; width: 22px; height: 22px;
    border-radius: 50%;
    background-color: #f7faff;
    transition: all 0.2s ease-in-out;
    border: none;
    box-shadow: 3px 3px 6px #c4d3e0, -3px -3px 6px #ffffff;
}
input[type="radio"]:hover { box-shadow: 4px 4px 8px #c4d3e0, -4px -4px 8px #ffffff; }
input[type="radio"]:active { transform: scale(0.95); }
input[type="radio"]:checked { box-shadow: inset 3px 3px 6px #c4d3e0, inset -3px -3px 6px #ffffff; }
input[type="radio"]::after {
    content: ''; position: absolute; transform: translate(-50%, -50%) scale(0);
    transition: transform 0.1s ease-in;
}
input[type="radio"]:checked::after {
    top: 50%; left: 50%; width: 10px; height: 10px; border-radius: 50%;
    background-color: #a7c7e7;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
    transform: translate(-50%, -50%) scale(1);
}

input[type="range"] {
    -webkit-appearance: none; appearance: none; width: 100%;
    background: transparent; cursor: pointer; outline: none;
}
input[type="range"]::-webkit-slider-runnable-track {
    width: 100%; height: 8px; background: #f7faff; border-radius: 4px;
    box-shadow: inset 2px 2px 4px #c4d3e0, inset -2px -2px 4px #ffffff;
}
input[type="range"]::-moz-range-track {
    width: 100%; height: 8px; background: #f7faff; border-radius: 4px;
    box-shadow: inset 2px 2px 4px #c4d3e0, inset -2px -2px 4px #ffffff;
    border: none;
}
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none; height: 30px; width: 30px; margin-top: -11px;
    background-image: url('https://files.catbox.moe/1lh5id.png');
    background-size: contain; background-repeat: no-repeat; background-position: center;
