    <div class="cat-collapsible" id="catCollapsible">
      <div class="cat-header" onclick="toggleCollapsible()">
        <div class="cat-title">
          <img src="https://files.catbox.moe/6bjhoo.png" alt="小猫神" class="cat-title-icon">
          吾乃小猫之神...
        </div>
        <div class="toggle-icon"></div>
      </div>
      <div class="cat-content">
        <div class="cat-content-inner">$1</div>
        <div class="cat-footer-toggle">₍^˶-𖥦-˵^₎੭点我收起喵~</div>
      </div>
    </div>

    <script>
      const catIconUrls = [
        'https://i.postimg.cc/wM5fdRwD/retouch-2025082000312081.png', 
        'https://i.postimg.cc/25JXdDqL/retouch-2025082002164365.png', 
        'https://i.postimg.cc/hjdZp5xy/retouch-2025082002164425.png', 
        'https://i.postimg.cc/gjbS5HHY/retouch-2025082002164465.png', 
        'https://i.postimg.cc/fR8PLXDK/retouch-2025082002164500.png', 
        'https://i.postimg.cc/Qxrwnhfp/retouch-2025082002164543.png', 
        'https://i.postimg.cc/3wWcDRwH/retouch-2025082002164571.png', 
        'https://i.postimg.cc/RCt2RNLS/retouch-2025082002164601.png', 
        'https://i.postimg.cc/c4Mjp2zP/retouch-2025082002164624.png', 
        'https://i.postimg.cc/4xfMswky/retouch-2025082002164656.png',
      ];

      const catIconElement = document.querySelector('.cat-title-icon');

      function setRandomIcon() {
        if (!catIconElement) return;

        const randomIndex = Math.floor(Math.random() * catIconUrls.length);
        const newIconUrl = catIconUrls[randomIndex];

        catIconElement.style.opacity = 0;

        setTimeout(() => {
          catIconElement.src = newIconUrl;
          catIconElement.style.opacity = 1;
        }, 50);
      }


      function toggleCollapsible() {
        const collapsible = document.getElementById('catCollapsible');
        const content = collapsible.querySelector('.cat-content');

        if (collapsible.classList.contains('expanded')) {
          collapsible.classList.remove('expanded');
          content.style.maxHeight = '0';
        } else {

          setRandomIcon(); 

          collapsible.classList.add('expanded');
          const height = content.scrollHeight;
          content.style.maxHeight = height + 'px';
        }

        setTimeout(updateIframeHeight, 400);
      }

      window.addEventListener('DOMContentLoaded', () => {
        formatParagraphs();
        setRandomIcon(); 
      });

      // --- 以下是原来的功能性代码，保持不变 ---
      document.querySelector('.cat-footer-toggle').addEventListener('click', toggleCollapsible);
      function formatParagraphs() {
        const contentDiv = document.querySelector('.cat-content-inner');
        if (!contentDiv) return;
        const rawText = contentDiv.textContent.trim();
        const paragraphs = rawText.split('\n').filter(p => p.trim() !== '');
        const newHtml = paragraphs.map(p => `<p>${p.trim()}</p>`).join('');
        contentDiv.innerHTML = newHtml;
      }
      function updateIframeHeight() {
        const bodyHeight = document.body.scrollHeight;
        if (window.parent && window.parent !== window) {
          window.parent.postMessage({ type: 'resizeIframe', height: bodyHeight }, '*');
        }
      }
      document.addEventListener('keydown', function (event) {
        if (event.key === 'Enter' || event.key === ' ') {
          const activeElement = document.activeElement;
          if (activeElement && activeElement.classList.contains('cat-header')) {
            event.preventDefault(); toggleCollapsible();
          }
        }
      });
      document.querySelector('.cat-header').setAttribute('tabindex', '0');
    </script>
  </body>
