<script>
        // 音乐播放器控制
        const audio = document.getElementById('background-music');
        const playPauseButton = document.getElementById('play-pause-button');
        const icon = playPauseButton.querySelector('i');

        // 尝试自动播放
        let playPromise = audio.play();
        if (playPromise !== undefined) {
            playPromise.then(_ => {
                icon.classList.remove('fa-play');
                icon.classList.add('fa-pause');
            }).catch(error => {
                icon.classList.remove('fa-pause');
                icon.classList.add('fa-play');
            });
        }

        playPauseButton.addEventListener('click', () => {
            if (audio.paused) {
                audio.play();
                icon.classList.remove('fa-play');
                icon.classList.add('fa-pause');
            } else {
                audio.pause();
                icon.classList.remove('fa-pause');
                icon.classList.add('fa-play');
            }
        });

        // 确保在音频结束时，如果图标是暂停，它仍然是暂停
        audio.addEventListener('pause', () => {
            if (!audio.ended) {
                 icon.classList.remove('fa-pause');
                 icon.classList.add('fa-play');
            }
         });

         audio.addEventListener('playing', () => {
            icon.classList.remove('fa-play');
            icon.classList.add('fa-pause');
         });
         
        // 弹窗控制逻辑 - 修复后的代码
        document.addEventListener('DOMContentLoaded', function() {
            // 获取弹窗和按钮
            const caseFileModal = document.getElementById('case-file');
            const secretModal = document.getElementById('secret');
            const caseFileBtn = document.getElementById('case-file-btn');
            const secretBtn = document.getElementById('secret-btn');
            const closeBtns = document.querySelectorAll('.close-btn');
            
            // 打开"案宗"弹窗
            caseFileBtn.addEventListener('click', function() {
                caseFileModal.classList.add('active');
                document.body.style.overflow = 'hidden'; // 防止背景滚动
            });
            
            // 打开"隐秘之事"弹窗
            secretBtn.addEventListener('click', function() {
                secretModal.classList.add('active');
                document.body.style.overflow = 'hidden'; // 防止背景滚动
            });
            
            // 关闭弹窗
            closeBtns.forEach(btn => {
                btn.addEventListener('click', function() {
                    caseFileModal.classList.remove('active');
                    secretModal.classList.remove('active');
                    document.body.style.overflow = 'auto'; // 恢复滚动
                });
            });
            
            // 点击弹窗外部关闭弹窗
            [caseFileModal, secretModal].forEach(modal => {
