            background: #1a1a1a;
            min-height: 100vh;
        }

        .status-overlay {
            background: url('http://sharkpan.xyz/f/2wKTQ/Camera_XHS_17378833197161040g008310d58agl6m504a6u5up4dvtjieh8uh8.jpg') center/cover, rgba(0,0,0,0.7);
            padding: 12px;
            width: 100%;
        }

        .status-title {
            color: #1E90FF;
            font-size: 25px;
            font-weight: 600;
            margin-bottom: 12px;
            padding-bottom: 8px;
            border-bottom: 2px solid rgba(255,255,255,0.2);
            display: flex;
            align-items: center;
            gap: 8px;
        }

        .profile-section {
            color: #fff;
            margin-bottom: 12px;
            display: grid;
            grid-template-columns: 1fr;
            gap: 8px;
        }

        .profile-item {
            display: flex;
            align-items: center;
            padding: 8px 10px;
            background: linear-gradient(to center, rgba(255,192,203,0.3), rgba(255,255,255,0.2));
            border: 2px solid rgba(255,192,203,0.5);
            border-radius: 15px;
            backdrop-filter: blur(5px);
            box-shadow: 0 0 10px rgba(255,192,203,0.2);
        }

        .profile-label {
            font-size: 13px;
            min-width: 55px;
            color: #1E90FF;
        }

        .profile-value {
            font-size: 13px;
            font-weight: 500;
            word-break: break-all;
            color: #1E90FF;
        }

        .video-section {
            width: 100%;
            aspect-ratio: 9/16;
            background: #000;
            border-radius: 6px;
            overflow: hidden;
            position: relative;
        }

        .video-container {
            width: 100%;
            height: 100%;
            position: relative;
        }

        .video-player {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        .video-overlay {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            z-index: 1;
        }

        .top-bar {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            padding: 8px;
            background: linear-gradient(180deg, rgba(0,0,0,0.5) 0%, transparent 100%);
            display: flex;
            justify-content: space-between;
            align-items: center;
            color: #fff;
            font-size: 13px;
        }

        .left-controls {
            display: flex;
            align-items: center;
            gap: 8px;
        }

        .interaction-bar {
            position: absolute;
            right: 5px;
            bottom: 70px;
            display: flex;
            flex-direction: column;
            gap: 12px;
            align-items: center;
        }

        .author-avatar {
            width: 35px;
            height: 35px;
            border-radius: 50%;
            overflow: hidden;
            border: 1.5px solid rgba(255,255,255,0.2);
        }

        .author-avatar img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        .interaction-button {
            color: #fff;
            text-align: center;
        }

        .interaction-icon {
            font-size: 18px;
            margin-bottom: 3px;
        }

        .interaction-count {
            font-size: 10px;
        }

        .bottom-info {
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            padding: 12px;
            background: linear-gradient(0deg, rgba(0,0,0,0.6) 0%, transparent 100%);
            color: #fff;
        }

        .author-name {
            font-size: 13px;
            margin-bottom: 4px;
            font-weight: 500;
        }

        .video-description {
            font-size: 11px;
            margin-bottom: 4px;
            line-height: 1.3;
        }

        .music-info {
            font-size: 10px;
            opacity: 0.8;
        }

        .watermark {
            position: relative;
            margin-top: 15px;
            padding: 15px;
            background: rgba(0,191,255,0.3);
            border-radius: 10px;
            color: #FF0000;
            font-size: 14px;
            font-weight: bold;
            text-align: center;
            line-height: 1.5;
            backdrop-filter: blur(5px);
            box-shadow: 0 0 15px rgba(0,191,255,0.2);
        }

        .watermark-text {
            text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
        }

        @media screen and (min-width: 768px) {
            .status-overlay {
                max-width: 400px;
                margin: 0 auto;
            }

            .profile-section {
                grid-template-columns: repeat(2, 1fr);
            }
        }
    </style>
</head>
