}

/* 菜单背景 */
.drawer-content {
  background: #fff1234;
background-image: none !important; /* 移除可能的背景图片 */
backdrop-filter: blur(5px); /* 添加高斯模糊效果，10px 可调整 */
  -webkit-backdrop-filter: blur(10px); /* 兼容 Safari 浏览器 */
}

/* 通用容器样式 */
.drawer-icon {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  padding-left: 12px;
}

/* ================= 通用图标样式 ================= */
.drawer-icon::before,
#leftNavDrawerIcon::before,
#rightNavDrawerIcon::before,
#sys-settings-button .drawer-icon::before,
#advanced-formatting-button .drawer-icon::before,
#WI-SP-button .drawer-icon::before,
#user-settings-button .drawer-icon::before,
#logo_block .drawer-icon::before,
#extensions-settings-button .drawer-icon::before,
#table_drawer_icon.drawer-icon::before,
#persona-management-button .drawer-icon::before {
  font-size: 20px;
  margin-right: 6px;
  margin-left: -4px;
  color: var(--SmartThemeBodyColor);
  vertical-align: middle;
  line-height: 1;
}

/* ================= 按钮图标（具体 emoji 替换） ================= */

/* 左侧导航 */
#leftNavDrawerIcon::before {
  content: '🍋'; /* 导航菜单 */
}

/* 系统设置 */
#sys-settings-button .drawer-icon::before {
  content: '✤'; /* 设置 */
}

/* 高级格式按钮 */
#advanced-formatting-button .drawer-icon::before {
  content: '🥕'; /* 工具 */
}

/* 世界信息 */
#WI-SP-button .drawer-icon::before {
  content: '✤'; /* 世界信息 */
}

/* 用户设置 */
#user-settings-button .drawer-icon::before {
  content: '🍋'; /* 用户头像/设置 */
}

/* Logo */
#logo_block .drawer-icon::before {
  content: '✤'; /* Logo 智能象征 */
}

/* 扩展设置 */
#extensions-settings-button .drawer-icon::before {
  content: '🥕'; /* 插件/扩展 */
}

/* 2.0记忆表格图标 */
#table_drawer_icon.drawer-icon::before {
  content: '✤'; /* 表格图标 */
}

/* 角色管理 */
#persona-management-button .drawer-icon::before {
  content: '✤'; /* AI角色管理 */
}

/* 右侧导航 */
#rightNavDrawerIcon::before {
  content: '🍋'; /* 右侧导航菜单 */
}

/* ================= 替代 .fa-* 按钮图标 ================= */

/* 菜单按钮（.fa-bars） */
.fa-bars::before {
  content: '❤︎';
  font-size: 22px;
  color: var(--SmartThemeBodyColor);
}

/* 扩展按钮 */
#extensionsMenuButton::before {
  content: '🎵';
  font-size: 22px;
  color: var(--SmartThemeBodyColor);
}

/* 发送按钮 */
.fa-paper-plane::before {
  content: '✦';
  font-size: 22px;
  color: var(--SmartThemeBodyColor);
}

/* 正则优化：针对 .regex-script-label 类元素的样式调整，通常用于标签或按钮布局 */
.regex-script-label {
  --flex-wrap: wrap; /* 允许 flex 容器中的子元素换行，覆盖可能的 nowrap 设置 */
  border-radius: var(--genericRadius) !important; /* 设置圆角半径，使用自定义变量 --genericRadius；!important 提升优先级 */
  background-color: var(--buttonFill) !important; /* 设置背景色为自定义变量 --buttonFill；!important 提升优先级 */
  gap: 2px !important; /* 设置 flex 子元素之间的间距为 4 像素；!important 提升优先级 */
  padding-top: 2px !important; /* 设置顶部内边距为 8 像素；!important 提升优先级 */
  padding-bottom: 4px !important; /* 设置底部内边距为 4 像素；!important 提升优先级 */
  margin-right: 35px !important; /* 设置右侧外边距为 24 像素；!important 提升优先级 */
}

/* 强制所有禁用状态文字保持原色 - 增强版 */
*:disabled, 
*[disabled], 
*[aria-disabled="true"], 
*[readonly],
.disabled, 
.inactive,
.disabled *,
.inactive * {
    color: inherit !important;
    -webkit-text-fill-color: inherit !important;
    text-fill-color: inherit !important;
    opacity: 1 !important;
    filter: none !important;
}

/* 覆盖所有输入类型 */
input:disabled,
input[disabled],
textarea:disabled,
textarea[disabled],
select:disabled,
select[disabled] {
    color: inherit !important;
    -webkit-text-fill-color: inherit !important;
    text-fill-color: inherit !important;
    -webkit-opacity: 1 !important;
    -moz-opacity: 1 !important;
    opacity: 1 !important;
}

/* 覆盖浏览器默认阴影 */
input:-webkit-autofill,
input:-webkit-autofill:hover, 
input:-webkit-autofill:focus,
input:-webkit-autofill:active {
    -webkit-text-fill-color: inherit !important;
    box-shadow: 0 0 0 1000px transparent inset !important;
}

/* 覆盖所有伪元素 */
::placeholder,
::-webkit-input-placeholder,
::-moz-placeholder,
:-ms-input-placeholder,
input::placeholder,
textarea::placeholder {
    color: inherit !important;
    opacity: 0.7 !important;
}

/* 特殊框架覆盖 */
[class*="disabled"]:not(.keep-style),
[class*="disabled"] *:not(.keep-style),
[class*="is-disabled"]:not(.keep-style),
[class*="is-disabled"] *:not(.keep-style) {
    color: inherit !important;
    opacity: 1 !important;
}

/* 强制光标样式 */
:disabled {
    cursor: not-allowed !important;
}

/* 固定图案颜色 */
.drawer-icon.closedIcon {
    opacity: 0.5;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
    opacity: 1;
}

/* 设置预设管理器的标题文字颜色 */
#completion_prompt_manager .completion_prompt_manager_header {
  color: var(--SmartThemeBodyColor);
}

/* 角色收藏滚动条 */
#HotSwapWrapper > div {
    overflow: auto hidden;
    flex-wrap: nowrap;
    justify-content: flex-start;
    gap: 8px;
    min-height: calc(var(--avatar-base-height)* 1.5);
}

#HotSwapWrapper > div::-webkit-scrollbar-thumb {
    background-color: #00000030; /* Lightened scrollbar thumb */
}

#HotSwapWrapper {
    overflow: hidden;
}

/* 移除顶部菜单栏下方的阴影线 */
.nav-menu-bar,
#top-nav,
#top-bar,
.nav-header,
.icon-menu-bar,
#icon-bar {
  box-shadow: none !important;
  border-bottom: none !important;
}

/* 新增装饰层，菜单栏花边 */
.nav-menu-bar::after,
#top-nav::after,
#top-bar::after {
  content: '';
  position: absolute;
  bottom: -30px; /* 对齐底部边框 */
  left: 0;
  width: 100%;
  height: 30px; /* 根据图片高度调整 */
  background-image: url('https://i.postimg.cc/YS5cShkF/5.png');
  background-size: 100% 100%; /* 宽度绑定导航栏 */
  background-repeat: no-repeat;
  z-index: -1;
  opacity: 1;
}

/* 移动端适配 */
@media (max-width: 768px) {
  .nav-menu-bar::after,
  #top-nav::after {
    height: 18px; /* 缩小高度适应移动端 */
    background-size: cover; /* 保持图片比例 */
  }
}

/* 按钮样式 - 添加最少样式以确保伪元素显示 */
.swipe_left,
.swipe_right {
    display: inline-flex !important; /* 使用 inline-flex 确保伪元素可居中显示 */
    width: 40px !important; /* 按钮宽度，确保伪元素有足够空间 */
    height: 40px !important; /* 按钮高度，确保图片和偏移量有空间 */
    font-size: 0 !important; /* 隐藏原始文本或图标 */
    color: transparent !important; /* 确保文本不可见 */
    opacity: 1 !important; /* 确保按钮常亮 */
    filter: none !important; /* 移除滤镜，确保无遮罩 */
}

