  font-size: 0.8em;
  color: #888;
  border-top: 1px solid rgba(0, 0, 0, 0.05);
  position: relative;
}

.clean-footer::before {
  content: '🐾🐾🐾';
  position: absolute;
  top: -15px;
  left: 0;
  right: 0;
  text-align: center;
  font-size: 12px;
  letter-spacing: 5px;
  opacity: 0.3;
}

/* 背景装饰元素 */
.cat-decoration {
  position: absolute;
  opacity: 0.05;
  z-index: 0;
}

.cat-decoration.top-right {
  top: 10px;
  right: 10px;
  font-size: 60px;
}

.cat-decoration.bottom-left {
  bottom: 10px;
  left: 10px;
  font-size: 50px;
  transform: rotate(30deg);
}
</style>
</head>
<body>
<div class="theme-clean-meow nova-container">
  <!-- 猫咪装饰元素 -->
  <div class="cat-decoration top-right">🐱</div>
  <div class="cat-decoration bottom-left">🐾</div>

  <div class="card-clean-meow">
    <div class="clean-header">
      <div>
        喵喵多选器
        <span>点击选项可多选，内容会追加到输入框</span>
      </div>
    </div>

    <div class="button-group-clean">
      <button class="action-btn-clean" data-action="$1">$1</button>
      <button class="action-btn-clean" data-action="$2">$2</button>
      <button class="action-btn-clean" data-action="$3">$3</button>
      <button class="action-btn-clean" data-action="$4">$4</button>
    </div>

    <div class="clean-footer">
      <div>
        <span class="cat-icon">✨</span> 轻点选项即可追加 <span class="cat-icon">✨</span>
      </div>
    </div>
  </div>
</div>

<script>
// 增强版多选功能
function appendToInput(text) {
  try {
    // 方法1：优先使用SillyTavern的appendinput命令
    if (window.parent && window.parent.triggerSlash) {
      window.parent.triggerSlash(`/appendinput ${text}`);
      return;
    }

    // 方法2：通用输入框处理
    const input = window.parent.document.querySelector('#send_textarea') || 
                 document.querySelector('textarea[placeholder*="发送消息"]') ||
                 document.querySelector('input[type="text"]');

    if (input) {
      // 智能追加内容（自动添加空格分隔）
      const currentValue = input.value.trim();
      input.value = currentValue ? `${currentValue} ${text}` : text;

      // 触发输入事件（确保UI更新）
      input.dispatchEvent(new Event('input', { bubbles: true }));
      input.focus();

      // 输入框动画反馈
      input.style.transform = 'scale(1.02)';
      setTimeout(() => input.style.transform = '', 200);
    }
  } catch (error) {
    console.error('追加内容时出错:', error);
  }
}

// 选项点击处理（带视觉反馈）
document.querySelectorAll('.action-btn-clean').forEach(btn => {
  btn.addEventListener('click', function() {
    const text = this.getAttribute('data-action');

    // 视觉反馈：切换激活状态
    this.classList.toggle('active');

    // 按钮动画
    this.style.transform = 'scale(0.96)';
    setTimeout(() => {
      this.style.transform = this.classList.contains('active') 
        ? 'translateY(-2px)' 
        : '';
    }, 200);

    // 追加到输入框
    appendToInput(text);
  });
});

// 页面加载动画
document.addEventListener('DOMContentLoaded', () => {
  const card = document.querySelector('.card-clean-meow');
  if (card) {
    card.style.opacity = '0';
    card.style.transform = 'translateY(10px)';
    card.style.transition = 'all 0.4s ease-out';

    setTimeout(() => {
      card.style.opacity = '1';
      card.style.transform = 'translateY(0)';
    }, 100);
  }
});
</script>
</body>
</html>
