#sys-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
#advanced-formatting-button .drawer-icon { background-image: url('https://files.catbox.moe/midwjr.png'); }
#WI-SP-button .drawer-icon { background-image: url('https://files.catbox.moe/w55n6b.png'); }
