#nonQRFormItems {
    position: relative;
}

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important;
}

#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "⟡ 思念送達中······ Load ♡⟡ ";
    color: #aed581; 
    left: 100px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-style: italic;
    font-size: 0.4em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap; 
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
}

input[type="checkbox"] {
    appearance: none !important; -webkit-appearance: none !important; font-size: 0 !important; color: transparent !important;
    position: relative; cursor: pointer;
    vertical-align: middle; margin-right: 8px; width: 22px; height: 22px;
    border-radius: 8px;
    background-color: #f8fbf7; 
    transition: all 0.2s ease-in-out;
    border: none;
    box-shadow: 3px 3px 6px #dbe4d5, -3px -3px 6px #ffffff; 
}
input[type="checkbox"]:hover {
    box-shadow: 4px 4px 8px #dbe4d5, -4px -4px 8px #ffffff; 
}
input[type="checkbox"]:active { transform: scale(0.95); }
input[type="checkbox"]:checked {
    box-shadow: inset 3px 3px 6px #dbe4d5, inset -3px -3px 6px #ffffff; 
}
input[type="checkbox"]::after {
    content: ''; position: absolute; transform: translate(-50%, -50%) scale(0);
    transition: transform 0.1s ease-in;
}
input[type="checkbox"]:checked::after {
    top: 50%; left: 50%; width: 12px; height: 12px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="%237cb342" stroke-width="3" stroke-linecap="round" stroke-linejoin="round"><polyline points="20 6 9 17 4 12"></polyline></svg>'); 
    background-size: contain; background-repeat: no-repeat;
    transform: translate(-50%, -50%) scale(1);
}

input[type="radio"] {
    appearance: none; -webkit-appearance: none; position: relative; cursor: pointer;
    vertical-align: middle; margin-right: 8px; width: 22px; height: 22px;
    border-radius: 50%;
    background-color: #f8fbf7; 
    transition: all 0.2s ease-in-out;
    border: none;
    box-shadow: 3px 3px 6px #dbe4d5, -3px -3px 6px #ffffff; 
}
input[type="radio"]:hover {
    box-shadow: 4px 4px 8px #dbe4d5, -4px -4px 8px #ffffff; 
}
input[type="radio"]:active { transform: scale(0.95); }
input[type="radio"]:checked {
    box-shadow: inset 3px 3px 6px #dbe4d5, inset -3px -3px 6px #ffffff; 
}
input[type="radio"]::after {
    content: ''; position: absolute; transform: translate(-50%, -50%) scale(0);
    transition: transform 0.1s ease-in;
}
input[type="radio"]:checked::after {
    top: 50%; left: 50%; width: 10px; height: 10px; border-radius: 50%;
    background-color: #8bc34a; 
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
    transform: translate(-50%, -50%) scale(1);
}

input[type="range"] {
    -webkit-appearance: none; appearance: none; width: 100%;
    background: transparent; cursor: pointer; outline: none;
}
input[type="range"]::-webkit-slider-runnable-track {
    width: 100%; height: 8px; background: #f8fbf7; border-radius: 4px; 
    box-shadow: inset 2px 2px 4px #dbe4d5, inset -2px -2px 4px #ffffff; 
}
input[type="range"]::-moz-range-track {
    width: 100%; height: 8px; background: #f8fbf7; border-radius: 4px; 
    box-shadow: inset 2px 2px 4px #dbe4d5, inset -2px -2px 4px #ffffff; 
    border: none;
}
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none; height: 30px; width: 30px; margin-top: -11px;
    background-image: url('https://files.catbox.moe/1lh5id.png');
    background-size: contain; background-repeat: no-repeat; background-position: center;
    background-color: transparent; border: none; box-shadow: none;
    transition: all 0.15s ease-in-out;
}
input[type="range"]::-moz-range-thumb {
    height: 30px; width: 30px;
    background-image: url('https://files.catbox.moe/1lh5id.png');
    background-size: contain; background-repeat: no-repeat; background-position: center;
    background-color: transparent; border: none; box-shadow: none;
    transition: all 0.15s ease-in-out;
}
input[type="range"]:hover::-webkit-slider-thumb,
input[type="range"]:hover::-moz-range-thumb {
    transform: scale(1.15);
}
input[type="range"]:active::-webkit-slider-thumb,
input[type="range"]:active::-moz-range-thumb {
    transform: scale(1.05);
}

.menu_button {
    background: #f8fbf7; border: none; border-radius: 10px; 
    color: #757575; 
    transition: all 0.2s ease-in-out;
    box-shadow: 5px 5px 10px #dbe4d5, -5px -5px 10px #ffffff; 
}
.menu_button:hover {
    transform: translateY(-2px);
    color: #616161; 
    box-shadow: 7px 7px 14px #dbe4d5, -7px -7px 14px #ffffff; 
}
.menu_button:active {
    transform: translateY(0);
    color: #424242; 
    box-shadow: inset 5px 5px 10px #dbe4d5, inset -5px -5px 10px #ffffff; 
}
.menu_button.redWarningBG, .menu_button.red_button {
    color: var(--accent-green); 
    box-shadow: 5px 5px 10px var(--accent-green-shadow), -5px -5px 10px #ffffff; 
}
.menu_button.redWarningBG:hover, .menu_button.red_button:hover {
    color: #7cb342; 
    box-shadow: 7px 7px 14px var(--accent-green-shadow), -7px -7px 14px #ffffff; 
}
.menu_button.redWarningBG:active, .menu_button.red_button:active {
    color: #689f38; 
    box-shadow: inset 5px 5px 10px var(--accent-green-shadow), inset -5px -5px 10px #ffffff; 
}

.text_pole, textarea, select {
    background-color: #f8fbf7; border: none; border-radius: 10px; 
    color: #757575;
    font-family: inherit; padding: 10px 15px;
    transition: all 0.2s ease-in-out;
    width: 100%; box-sizing: border-box;
    box-shadow: inset 3px 3px 6px #dbe4d5, inset -3px -3px 6px #ffffff; 
}
.text_pole:hover, textarea:hover, select:hover {
    box-shadow: inset 4px 4px 7px #dbe4d5, inset -4px -4px 7px #ffffff; 
}
.text_pole:focus, textarea:focus, select:focus {
    outline: none;
    box-shadow: inset 3px 3px 6px #dbe4d5, inset -3px -3px 6px #ffffff, 0 0 0 3px var(--accent-green-shadow);
}
::placeholder {
    color: #c5c5c5;
    opacity: 1;
}
select {
    -webkit-appearance: none; -moz-appearance: none; appearance: none;
    padding-right: 40px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="%23757575" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><polyline points="6 9 12 15 18 9"></polyline></svg>');
    background-repeat: no-repeat; background-position: right 15px center; background-size: 16px;
}
textarea { resize: vertical; }

.drawer-content {
    background-image: none;
    background-color: #f8fbf7;
    border: none; border-radius: 15px;
    box-shadow: 8px 8px 16px #dbe4d5, -8px -8px 16px #ffffff; 
    transition: box-shadow 0.3s ease-in-out;
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    border: none; background-image: none; overflow: visible;
    background-color: #f8fbf7;
    border-radius: 12px; transition: all 0.2s ease-in-out; position: relative;
    box-shadow: 5px 5px 10px #dbe4d5, -5px -5px 10px #ffffff; 
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt::before {
    content: none;
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:not(.completion_prompt_manager_prompt_disabled):hover {
    transform: translateY(-2px);
    box-shadow: 7px 7px 14px #dbe4d5, -7px -7px 14px #ffffff; 
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:not(.completion_prompt_manager_prompt_disabled):active {
    transform: translateY(0);
    box-shadow: inset 5px 5px 10px #dbe4d5, inset -5px -5px 10px #ffffff; 
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt_disabled {
    opacity: 0.7; background-color: #f8fbf7; 
    box-shadow: inset 2px 2px 5px #dbe4d5, inset -2px -2px 5px #ffffff; 
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    text-shadow: none; color: #757575; position: relative; z-index: 2; 
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #c5c5c5; 
}

::-webkit-scrollbar { width: 14px; height: 14px; }
::-webkit-scrollbar-track {
    background: #f8fbf7; border-radius: 10px;
    box-shadow: inset 2px 2px 5px #dbe4d5, inset -2px -2px 5px #ffffff;
}
::-webkit-scrollbar-thumb {
    background: #f8fbf7; border-radius: 10px; border: 2px solid #f8fbf7;
    background-clip: padding-box;
    box-shadow: 3px 3px 6px #dbe4d5, -3px -3px 6px #ffffff;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #f0f4f0;
    box-shadow: 4px 4px 8px #dbe4d5, -4px -4px 8px #ffffff;
}
:root {
  scrollbar-width: thin;
  scrollbar-color: #c5c5c5 #f8fbf7;
}

h3 {
    color: #616161;
    font-size: 1.4em; font-weight: 600; margin: 20px 0 10px 0;
    letter-spacing: 0.5px;
    text-shadow: 1px 1px 2px #dbe4d5, -1px -1px 2px #ffffff;
}
h4 {
    color: #9e9e9e;
    font-size: 1.0em; font-weight: 700; margin: 18px 0 8px 0;
    letter-spacing: 1px; text-transform: uppercase;
    text-shadow: 1px 1px 1px #dbe4d5, -1px -1px 1px #ffffff;
}

#top-bar {
    background-color: #ffffff;
    border: 1px solid #f0f0f0;
    padding: 8px 15px;
    width: calc(100% - 20px);
    border-radius: 20px;
    box-sizing: border-box;
    box-shadow: 0 4px 12px rgba(210, 225, 200, 0.5);
    transition: all 0.2s ease-in-out;
    color: var(--text-primary);
}

#form_sheld {
    position: absolute;
    bottom: 10px;
    width: 100%;
    display: flex;
    justify-content: center;
}
.last_mes {
    margin-bottom: 90px !important;
}

#send_form, body.no-blur #send_form {
    width: 90%;
    max-width: 800px;
    background-color: #ffffff;
    border: 1px solid #f0f0f0;
    border-radius: 20px;
    padding: 8px 15px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 12px rgba(210, 225, 200, 0.5);
    backdrop-filter: none;
    -webkit-backdrop-filter: none;
}

#send_form:hover, 
body.no-blur #send_form:hover,
#send_form:has(#send_textarea:focus) {
    transform: translateY(-2px);
    box-shadow: 0 6px 18px rgba(210, 225, 200, 0.6);
}

#send_textarea {
    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;
    color: var(--text-primary);
    padding: 8px 0;
}
#rightSendForm i, #leftSendForm i {
    color: var(--text-secondary);
    transition: color 0.3s ease;
}
#rightSendForm i:hover, #leftSendForm i:hover {
    color: var(--text-primary);
}

#qr--bar > .qr--buttons .qr--button { filter: none; }
#qr--bar { height: 0.1px; }
#send_form:hover #qr--bar { height: 40px; transition: all 0.6s ease; }

