}

/* 6.聊天气泡 */
/* 6.1气泡*/
.mes {
  width: 92% !important;/*宽度*/
  max-width: 1200px /*占界面多宽*/!important;
/*↓上下左右边距*/
  margin-left: 0px !important;
  margin-top: 90px !important;
  margin-bottom: 30px;
  background: linear-gradient(
    135deg,
    rgba(255, 240, 245, 0.6),
    rgba(240, 250, 255, 0.5)
  ) !important;
  padding: 0.6rem 1rem 0.6rem 1rem !important;
  border: 2px solid #F4F5E9 !important;
  border-radius: 22px 22px 18px 18px !important;
  box-shadow: 0 2px 6px rgba(255, 203, 219, 0.2) !important;
  position: relative;
  z-index: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow: visible !important;
}

/* 6.2文本气泡 */
.mes_block {
  position: relative;
  isolation: isolate;
  background: rgba(255, 255, 255, 0.6);
  border-radius: 18px !important;
/*↓内框边距*/
  padding-top: 50px !important;          
  padding-bottom: 17px !important;
  padding-left: 13px !important;
  padding-right: 7px !important;
  margin-top: -67px;
  width: calc(100% + 10px) !important;   
  margin-left: 2px !important;           
  border: 2px dashed rgba(255, 183, 197, 0.5);
  box-shadow: 0 4px 12px rgba(255, 203, 219, 0.3);
  backdrop-filter: blur(8px);
  transition: all 0.3s ease;
}
/* 6.3文本调整 */
.mes_text {
  padding: 0 !important;
  margin: 0 !important;
  line-height: 1.5 !important;/*行间距*/
  letter-spacing: 0.3px;/*字间距*/
  word-break: break-word;
  padding-bottom: 0em !important;
}

/* 6.4字体颜色 */
.mes[is_user="true"] .mes_text {
  color: #e6acc6; 
}/*你的*/
.mes:not([is_user="true"]) .mes_text {
  color: #5a6167; 
}/*ai的*/
#chat {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-left: 0 !important;
}
/*6.5气泡间隔*/
#chat .mes {
  top: -60px;
  bottom: 30px;
}/*上下*/
#chat .mes:first-child {
  top: -30px !important; /*首楼距离上方*/ 
}
/*7.名字框 */
body.drop_target.no_animation.translate.caption.sd.tts.bubblechat .mes .ch_name {
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 4px;
  left: 0px;
  right: 0;
  font-size: 0.95rem;
  font-weight: bold;
  color: #8c5a68;
  height: 32px;
  padding: 3px 14px 0 36px;
  background: linear-gradient(to right, #ffe5f2, #e4fff4, transparent 90%);
  border-radius: 16px; 
  border: 1.5px solid rgba(255, 222, 236, 0.5);
  pointer-events: auto;
  z-index: 99;
  text-indent: 4px;
  line-height: 1.6;
}
body.drop_target.no_animation.translate.caption.sd.tts.bubblechat .mes .ch_name::before {
  content: "";
  position: absolute;
  left: 17px;
  width: 20px;
  height: 20px;
  background-image: url('https://s3.bmp.ovh/imgs/2025/07/25/f50fd181b1196e6d.png');
  background-size: 29px;
  background-repeat: no-repeat;
  background-position: center;
  pointer-events: none;
}
/* 8.角落图片*/

@keyframes jelly-sway {
  0%   { transform: translateX(0); }
  25%  { transform: translateX(2px); }
  50%  { transform: translateX(0); }
  75%  { transform: translateX(-2px); }
  100% { transform: translateX(0); }
}

.mes_block {
  overflow: visible !important;
}

/*8.1 用户左下角图标 */
.mes[is_user="true"] .mes_block::after {
  content: "";
  position: absolute;
  bottom: 10px;
  left: 7px;
  width: 40px;
  height: 40px;
  background-image: url("https://p.sda1.dev/25/e752d6d3b62c51e0c828d5b662ee8939/1000011074.png");
  background-size: 45px;
  background-repeat: no-repeat;
  pointer-events: none;
  opacity: 0.9;
  animation: jelly-sway 3s ease-in-out infinite;
  z-index: -2;
}

/*8.2 AI 右下角图标 */
.mes:not([is_user="true"]) .mes_block::after {
  content: "";
  position: absolute;
  bottom: 7px;
  right: 4px;
  width: 40px;
  height: 40px;
  background-image: url("https://p.sda1.dev/25/43723cc0827aec530b88580aba4ceebe/1000010635.png");
  background-size: 41px;
  background-repeat: no-repeat;
  pointer-events: none;
  opacity: 0.9;
  animation: jelly-sway 3s ease-in-out infinite;
  z-index: -5;
}
/* 9.头像 */
/*9.1尺寸*/
body:not(.drop_target):not(.no_animation):not(.translate):not(.caption):not(.sd):not(.tts):not(.bubblechat) .avatar img,
body.drop_target.no_animation.translate.caption.sd.tts.bubblechat .mes .avatar img {
