  height: 100px !important;/*高*/
  object-fit: cover !important;
  border-radius: 50% !important;
  background: white;
  padding: 1px;
  position: relative;
  z-index: -9;
  box-shadow: 0 4px 8px rgba(174, 232, 255, 0.4) !important;
}

/* 9.1位置 */
body.drop_target.no_animation.translate.caption.sd.tts.bubblechat .mes .mesAvatarWrapper {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 100%;
  gap: 89px 10px;/*头像和下方文字间距，楼层等元素间距*/
