  margin-left: 3px; /* 和数字的间距 */
}

/* 楼层在第一行 */
.mesIDDisplay {
  width: 100%;
}
.tokenCounterDisplay,
.mes_timer {
    position: relative;
    top: -79px; /* 上距离 */
    margin-bottom: -20px; /* 下距离 */
}/*计时器、token位置*/

.mes_timer, .mesIDDisplay, .tokenCounterDisplay {
