/* 🍧 Toastr 强制背景图覆盖 + 清空全局影响 */
#toast-container .toast {
    background: none !important; /* 清除所有背景 */
    background-image: url('https://p.sda1.dev/26/cf0a81bfa71fd6b595a60f443b37e1af/1000014308.jpg') !important;
    background-size: cover !important;
    background-position: center !important;
    background-repeat: no-repeat !important;

    color: #4b2e36 !important;
    font-family: "Shanggu Round", sans-serif !important;
    font-size: 0.95rem !important;

    border: 2px solid #f9dbe8 !important;
    border-radius: 18px !important;
    box-shadow: 0 4px 12px rgba(255, 203, 219, 0.5) !important;
    padding: 12px 16px !important;
    backdrop-filter: blur(6px);
    opacity: 0.95;
    animation: candyFadeIn 0.5s ease forwards;
}

/* 动画 */
@keyframes candyFadeIn {
  0%   { opacity: 0; transform: translateY(-10px) scale(0.95); }
  100% { opacity: 0.95; transform: translateY(0) scale(1); }
}
/* ✧ Toastr 图标美化（修复放大裁切问题）✧ */
#toast-container .toast::before {
    content: "";
    position: absolute;
    left: 15px;
    top: -1px;
    width: 40px;
    height: 40px;
    background-image: url('https://p.sda1.dev/26/11fc68fdbb03ac391bc4a87dcfb965a8/1000014307.gif');
    background-size: contain; /* ✅ 不裁剪，完整显示 */
    background-repeat: no-repeat;
    background-position: center;
    z-index: 2;
    opacity: 0.95;
    animation: jellyIcon 2s ease-in-out infinite;
    pointer-events: none;
    overflow: visible;
}

/* 🧁 留出左边图标的内边距（加大） */
#toast-container .toast {
    padding-left: 50px !important;
    position: relative;
    overflow: visible;
}

/* 🍭 图标晃动动画 */
@keyframes jellyIcon {
    0%   { transform: scale(1) translateX(0); }
    25%  { transform: scale(1.1) translateX(1px); }
    50%  { transform: scale(1) translateX(0); }
    75%  { transform: scale(1.1) translateX(-1px); }
    100% { transform: scale(1) translateX(0); }
}
