/* === ★欢迎食用长青青的自定义美化★ === */
@import url("https://fontsapi.zeoseven.com/281/main/result.css");
@import url("https://fontsapi.zeoseven.com/441/main/result.css");

body {
  font-family: "Xiaolai SC"; 
  font-weight: normal;
}
.name_text {
  font-family: "JiangChengJieXingTi"; 
  color: #D6ABD1; 
  padding-bottom: 0;
}
/*  🐰可直接使用拉杆部分调整颜色，头图可使用猫箱/postimg等图床链接进行替换🐰  */
/*  头像框更改，不同色系的头像框在css结尾 */
.mesAvatarWrapper .avatar::after {
  content: "";
  position: absolute;
  top: 50% !important;/*⭐ 头像框上下调节*/
  left: 50% !important;/*⭐ 头像框左右调节*/
}

.mes[is_user='true'] {
  --avatar-deco: url('https://files.catbox.moe/ndt05n.png');/*⭐ 替换这里 */
  --frame-width-ratio: 1.28;/*⭐两个1.8都是大小调节 */
  --frame-height-ratio: 1.28;
}

.mes[is_user='false'] {
  --avatar-deco: url('https://files.catbox.moe/mxs8n8.PNG');/*⭐ 替换这里 */
  --frame-width-ratio: 1.28;/*⭐两个1.8都是大小调节 */
  --frame-height-ratio: 1.28;
}

/* 头图自定义  */
/* ===== user头图样式 ===== */
.mes[is_user='true'] .mes_block::before {
  content: '';
  position: absolute;
  top: 0px;
  left: 50%;
  transform: translateX(-50%);
  width: calc(100% + 16px);
  height: 150px;
  background:
url('https://files.catbox.moe/jzjl5n.png') center/cover no-repeat;/*⭐ 替换这里 */
  mask: linear-gradient(to bottom, rgba(0,0,0,1) 0%, rgba(0,0,0,0) 100%);
  mask-composite: intersect;
  z-index: 1;
  pointer-events: none !important;
}

/* ===== char头图样式 ===== */
.mes[is_user='false'] .mes_block::before {
  content: '';
  position: absolute;
  top: 0px;
  left: 50%;
  transform: translateX(-50%);
  width: calc(100% + 16px);
  height: 150px;
  background:
url('https://files.catbox.moe/fjvsqn.png') center/cover no-repeat;/*⭐ 替换这里 */
  mask: linear-gradient(to bottom, rgba(0,0,0,1) 0%, rgba(0,0,0,0) 100%);
  mask-composite: intersect;
  z-index: 1;
  pointer-events: none !important;
}

/* === ★以下代码请斟酌改动★ === */

/* 按钮图标（emoji 可替换）  */

/* 左侧导航 */
#leftNavDrawerIcon::before {
